mapper.update:
UPDATE YAGL_YAJBXX
id=#{id}, yaid=#{yaid}, mbid=#{mbid}, yazl=#{yazl}, yabh=#{yabh}, yasxh=#{yasxh}, gxbbh=#{gxbbh}, yabbh=#{yabbh}, bmdj=#{bmdj}, yalxdm=#{yalxdm}, yamc=#{yamc}, dxid=#{dxid}, bwid=#{bwid}, yaztdm=#{yaztdm}, dxlxdm=#{dxlxdm}, sfkqy=#{sfkqy}, zzrid=#{zzrid}, zzrmc=#{zzrmc}, zzdwid=#{zzdwid}, zzdwmc=#{zzdwmc}, zzrq=TO_DATE(#{zzrq},'yyyy-MM-dd HH24:mi:ss'), czyid=#{czyid}, czyxm=#{czyxm}, jlzt=#{jlzt}, cszt=#{cszt}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, sfmya=#{sfmya}, jgid=#{jgid}, jgmc=#{jgmc}, dxmc=#{dxmc}, bwmc=#{bwmc}, sfwlsj=#{sfwlsj}, czjg=#{czjg}, sjbb=#{sjbb}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), ywxtbsid=#{ywxtbsid}, jksjbb=#{jksjbb}, tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss'), byzda=#{byzda}, byzdb=#{byzdb}, byzdc=#{byzdc}, byzdd=#{byzdd}, pkid=#{pkid}
WHERE PKID = #{pkid}

mapper.select:
SELECT

FROM YAGL_YAJBXX t
WHERE t.JLZT = '1'

	and t.yamc LIKE concat(concat('%',#{yamc}),'%')


	and t.dxmc LIKE concat(concat('%',#{dxmc}),'%')


	and t.yalxdm = #{yalxdm}


	and t.dxlxdm = #{dxlxdm}


	and t.yazl = #{yazl}


	and t.zzrq >= to_date(#{begintime_create},'yyyy/MM/dd')


	and t.zzrq <= to_date(#{endtime_create},'yyyy/MM/dd')
