mapper.insert:
INSERT INTO YAGL_YA_ZZDW(uuid,yabh,yasxh,yabbh,yalxdm,yamc,yazt,yajb,dxid,dxmc,bwid,bwmc,sfkqy,jgid,jgbm,jgmc,zzrid,zzrmc,zzsj,shzt,shrid,shrmc,shsj,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.yabh,jdbcType=VARCHAR} yabh,#{item.yasxh,jdbcType=VARCHAR} yasxh,#{item.yabbh,jdbcType=VARCHAR} yabbh,#{item.yalxdm,jdbcType=VARCHAR} yalxdm,#{item.yamc,jdbcType=VARCHAR} yamc,#{item.yazt,jdbcType=VARCHAR} yazt,#{item.yajb,jdbcType=VARCHAR} yajb,#{item.dxid,jdbcType=VARCHAR} dxid,#{item.dxmc,jdbcType=VARCHAR} dxmc,#{item.bwid,jdbcType=VARCHAR} bwid,#{item.bwmc,jdbcType=VARCHAR} bwmc,#{item.sfkqy,jdbcType=VARCHAR} sfkqy,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.jgbm,jdbcType=VARCHAR} jgbm,#{item.jgmc,jdbcType=VARCHAR} jgmc,#{item.zzrid,jdbcType=VARCHAR} zzrid,#{item.zzrmc,jdbcType=VARCHAR} zzrmc,#{item.zzsj,jdbcType=DATE} zzsj,#{item.shzt,jdbcType=VARCHAR} shzt,#{item.shrid,jdbcType=VARCHAR} shrid,#{item.shrmc,jdbcType=VARCHAR} shrmc,#{item.shsj,jdbcType=DATE} shsj,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_YA_ZZDW
yabh=#{yabh}, yasxh=#{yasxh}, yabbh=#{yabbh}, yalxdm=#{yalxdm}, yamc=#{yamc}, yazt=#{yazt}, yajb=#{yajb}, dxid=#{dxid}, dxmc=#{dxmc}, bwid=#{bwid}, bwmc=#{bwmc}, sfkqy=#{sfkqy}, jgid=#{jgid}, jgbm=#{jgbm}, jgmc=#{jgmc}, zzrid=#{zzrid}, zzrmc=#{zzrmc}, zzsj=TO_DATE(#{zzsj},'yyyy-MM-dd HH24:mi:ss'), shzt=#{shzt}, shrid=#{shrid}, shrmc=#{shrmc}, shsj=TO_DATE(#{shsj},'yyyy-MM-dd HH24:mi:ss'), cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}

mapper.select:
SELECT , c2.code_name yalxmc, c4.code_name yaztmc, c6.code_name yajbmc, c8.code_name shztmc FROM YAGL_YA_ZZDW t
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'YALX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.YALXDM = c2.CODE_VALUE
Left Join SYS_CODELIST c3 on c3.CODETYPE = 'YAZT' And c3.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c4 On c4.CODEID = c3.CODEID And c4.DELETE_FLAG = 'N' and t.YAZT = c4.CODE_VALUE
Left Join SYS_CODELIST c5 on c5.CODETYPE = 'YAJB' And c5.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c6 On c6.CODEID = c5.CODEID And c6.DELETE_FLAG = 'N' and t.YAJB = c6.CODE_VALUE
Left Join SYS_CODELIST c7 on c7.CODETYPE = 'YASHZT' And c7.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c8 On c8.CODEID = c7.CODEID And c8.DELETE_FLAG = 'N' and t.SHZT = c8.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
and t.yamc LIKE concat(concat('%',#{yamc}),'%')
and t.dxmc LIKE concat(concat('%',#{dxmc}),'%')
and t.dxid = #{dxid}
and t.yalxdm LIKE concat(rtrim(#{yalxdm},'0'),'%')
and t.yajb = #{yajb}
and t.shzt = #{shzt}
and TO_CHAR(t.shsj,'yyyy/MM/dd') >= #{begintime}
and TO_CHAR(t.shsj,'yyyy/MM/dd') <= #{endtime}
