mapper.select:

SELECT 

FROM YAGL_YA_ZQSD t 
WHERE t.DELETE_FLAG = 'N' AND  t.ZQID = #{value}

mapper.insert:
INSERT INTO YAGL_YA_ZQSD(zqid,yaid,zdbwid,jzid,zqbw,zqdj,qhyy,gyjzhzwxx,zhcs,rswz,qhbwgd,rsmj,zqms,zqsdyj,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.zqid,jdbcType=VARCHAR} zqid,#{item.yaid,jdbcType=VARCHAR} yaid,#{item.zdbwid,jdbcType=VARCHAR} zdbwid,#{item.jzid,jdbcType=VARCHAR} jzid,#{item.zqbw,jdbcType=VARCHAR} zqbw,#{item.zqdj,jdbcType=VARCHAR} zqdj,#{item.qhyy,jdbcType=VARCHAR} qhyy,#{item.gyjzhzwxx,jdbcType=VARCHAR} gyjzhzwxx,#{item.zhcs,jdbcType=VARCHAR} zhcs,#{item.rswz,jdbcType=VARCHAR} rswz,#{item.qhbwgd,jdbcType=DOUBLE} qhbwgd,#{item.rsmj,jdbcType=DOUBLE} rsmj,#{item.zqms,jdbcType=VARCHAR} zqms,#{item.zqsdyj,jdbcType=VARCHAR} zqsdyj,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_YA_ZQSD
zqid=#{zqid}, yaid=#{yaid}, zdbwid=#{zdbwid}, jzid=#{jzid}, zqbw=#{zqbw}, zqdj=#{zqdj}, qhyy=#{qhyy}, gyjzhzwxx=#{gyjzhzwxx}, zhcs=#{zhcs}, rswz=#{rswz}, qhbwgd=#{qhbwgd}, rsmj=#{rsmj}, zqms=#{zqms}, zqsdyj=#{zqsdyj}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
