mapper.select:
SELECT

FROM YAGL_WL_XFZBXX t
WHERE t.DELETE_FLAG = 'N' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO YAGL_WL_XFZBXX(uuid,zbbm,zbmc,ssdz,ssdzmc,xzqy,xssd,sccj,zcbl,kcsl,zzsl,wxsl,ztsl,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.zbbm,jdbcType=VARCHAR} zbbm,#{item.zbmc,jdbcType=VARCHAR} zbmc,#{item.ssdz,jdbcType=VARCHAR} ssdz,#{item.ssdzmc,jdbcType=VARCHAR} ssdzmc,#{item.xzqy,jdbcType=VARCHAR} xzqy,#{item.xssd,jdbcType=DOUBLE} xssd,#{item.sccj,jdbcType=VARCHAR} sccj,#{item.zcbl,jdbcType=DOUBLE} zcbl,#{item.kcsl,jdbcType=DOUBLE} kcsl,#{item.zzsl,jdbcType=DOUBLE} zzsl,#{item.wxsl,jdbcType=DOUBLE} wxsl,#{item.ztsl,jdbcType=DOUBLE} ztsl,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_WL_XFZBXX
uuid=#{uuid}, zbbm=#{zbbm}, zbmc=#{zbmc}, ssdz=#{ssdz}, ssdzmc=#{ssdzmc}, xzqy=#{xzqy}, xssd=#{xssd}, sccj=#{sccj}, zcbl=#{zcbl}, kcsl=#{kcsl}, zzsl=#{zzsl}, wxsl=#{wxsl}, ztsl=#{ztsl}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
