SELECT #{item.id,jdbcType=VARCHAR} id,#{item.zbqcbm,jdbcType=VARCHAR} zbqcbm,#{item.zbqcmc,jdbcType=VARCHAR} zbqcmc,#{item.zbqclxdm,jdbcType=VARCHAR} zbqclxdm,#{item.zbqclxmc,jdbcType=VARCHAR} zbqclxmc,#{item.cllxdm,jdbcType=VARCHAR} cllxdm,#{item.cllxmc,jdbcType=VARCHAR} cllxmc,#{item.sfxhxzb,jdbcType=VARCHAR} sfxhxzb,#{item.ggxh,jdbcType=VARCHAR} ggxh,#{item.jldwdm,jdbcType=VARCHAR} jldwdm,#{item.ckj,jdbcType=DOUBLE} ckj,#{item.zl,jdbcType=DOUBLE} zl,#{item.tj,jdbcType=DOUBLE} tj,#{item.cz,jdbcType=VARCHAR} cz,#{item.bzh,jdbcType=VARCHAR} bzh,#{item.jcph,jdbcType=VARCHAR} jcph,#{item.kssj,jdbcType=VARCHAR} kssj,#{item.zycf,jdbcType=VARCHAR} zycf,#{item.syfw,jdbcType=VARCHAR} syfw,#{item.zrjylbdm,jdbcType=VARCHAR} zrjylbdm,#{item.jkzbgndls,jdbcType=VARCHAR} jkzbgndls,#{item.shfwdw,jdbcType=VARCHAR} shfwdw,#{item.byzq,jdbcType=DOUBLE} byzq,#{item.jczq,jdbcType=DOUBLE} jczq,#{item.jlzt,jdbcType=DOUBLE} jlzt,#{item.cszt,jdbcType=DOUBLE} cszt,#{item.sjc,jdbcType=DATE} sjc,#{item.bz,jdbcType=VARCHAR} bz,#{item.cjsj,jdbcType=DATE} cjsj,#{item.sjbb,jdbcType=DOUBLE} sjbb,#{item.ywxtbsid,jdbcType=VARCHAR} ywxtbsid,#{item.vercol,jdbcType=DATE} vercol,#{item.tbjdh,jdbcType=VARCHAR} tbjdh,#{item.tbsjc,jdbcType=DATE} tbsjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3 FROM DUAL
