mapper.update:
UPDATE WL_ZBQCDY
pkid=#{pkid}, id=#{id}, zbqcbm=#{zbqcbm}, zbqcmc=#{zbqcmc}, zbqclxdm=#{zbqclxdm}, zbqclxmc=#{zbqclxmc}, cllxdm=#{cllxdm}, cllxmc=#{cllxmc}, sfxhxzb=#{sfxhxzb}, ggxh=#{ggxh}, jldwdm=#{jldwdm}, ckj=#{ckj}, zl=#{zl}, tj=#{tj}, cz=#{cz}, bzh=#{bzh}, jcph=#{jcph}, kssj=#{kssj}, zycf=#{zycf}, syfw=#{syfw}, zrjylbdm=#{zrjylbdm}, jkzbgndls=#{jkzbgndls}, shfwdw=#{shfwdw}, byzq=#{byzq}, jczq=#{jczq}, jlzt=#{jlzt}, cszt=#{cszt}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), sjbb=#{sjbb}, ywxtbsid=#{ywxtbsid}, vercol=TO_DATE(#{vercol},'yyyy-MM-dd HH24:mi:ss'), tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss')
WHERE ID = #{id}

