mapper.select:

SELECT
h.CSMC csmc
/*,h.CSLX*/
,h.JLDWDM jldw
,h.MRZ mrz
/*,m.CODEID
,p.CODE_NAME cslxmc*/
,h.GSID
FROM ZHJC_JSGS_CS h
/*LEFT JOIN (select GSID,CSMC,JLDWDM,MRZ from ZHJC_JSGS_CS where delete_flag = 'N') h
ON t.uuid = h.GSID*/
/*LEFT JOIN SYS_CODELIST m
ON m.CODETYPE = 'CSLX'
LEFT JOIN SYS_CODELIST_DETAIL p
ON m.CODEID = p.CODEID AND h.CSLX = p.CODE_VALUE*/
WHERE h.DELETE_FLAG = 'N' AND  h.gsid = #{value}

mapper.insert:
INSERT INTO ZHJC_JSGS(uuid,gsmc,gslb,gssm,jsgs,jsgsdw,sfqy,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.gsmc,jdbcType=VARCHAR} gsmc,#{item.gslb,jdbcType=VARCHAR} gslb,#{item.gssm,jdbcType=VARCHAR} gssm,#{item.jsgs,jdbcType=VARCHAR} jsgs,#{item.jsgsdw,jdbcType=VARCHAR} jsgsdw,#{item.sfqy,jdbcType=VARCHAR} sfqy,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE ZHJC_JSGS
uuid=#{uuid}, gsmc=#{gsmc}, gslb=#{gslb}, gssm=#{gssm}, jsgs=#{jsgs}, jsgsdw=#{jsgsdw}, sfqy=#{sfqy}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}

mapper.select:
SELECT
,p.CODE_NAME lbmc ,f.CODEID FROM ZHJC_JSGS t LEFT JOIN SYS_CODELIST f ON f.CODETYPE = 'GSLB' LEFT JOIN SYS_CODELIST_DETAIL p ON f.CODEID= p.CODEID AND t.GSLB = p.CODE_VALUE WHERE t.DELETE_FLAG = 'N'
and t.gsmc LIKE concat(concat('%',#{gsmc}),'%')
and t.gslb = #{gslb}
and t.sfqy = #{sfqy}

mapper.insert:
INSERT INTO ZHJC_JSGS_CS(uuid,gsid,csmc,cslxdm,jldwdm,mrz,sxh,cjrid,cjrmc,cjsj,delete_flag) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{gsid,jdbcType=VARCHAR} gsid,#{item.csmc,jdbcType=VARCHAR} csmc,#{item.cslxdm,jdbcType=VARCHAR} cslxdm,#{item.jldwdm,jdbcType=VARCHAR} jldwdm,#{item.mrz,jdbcType=VARCHAR} mrz,#{item.sxh,jdbcType=VARCHAR} sxh,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,SYSDATE cjsj,'N' delete_flag FROM DUAL
) A
