mapper.select:

SELECT 

FROM YAGL_WL_XFYJXX f 
WHERE f.DELETE_FLAG = 'N' AND  f.PKID = #{value}

mapper.insert:
INSERT INTO YAGL_WL_XFYJXX(uuid,yjbm,yjmc,ssdz,ssdzmc,xzqy,yjlx,sccj,pc,cbl,czl,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.yjbm,jdbcType=VARCHAR} yjbm,#{item.yjmc,jdbcType=VARCHAR} yjmc,#{item.ssdz,jdbcType=VARCHAR} ssdz,#{item.ssdzmc,jdbcType=VARCHAR} ssdzmc,#{item.xzqy,jdbcType=VARCHAR} xzqy,#{item.yjlx,jdbcType=DOUBLE} yjlx,#{item.sccj,jdbcType=VARCHAR} sccj,#{item.pc,jdbcType=VARCHAR} pc,#{item.cbl,jdbcType=DOUBLE} cbl,#{item.czl,jdbcType=DOUBLE} czl,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_WL_XFYJXX
uuid=#{uuid}, yjbm=#{yjbm}, yjmc=#{yjmc}, ssdz=#{ssdz}, ssdzmc=#{ssdzmc}, xzqy=#{xzqy}, yjlx=#{yjlx}, sccj=#{sccj}, pc=#{pc}, cbl=#{cbl}, czl=#{czl}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
