mapper.select:

SELECT 

FROM YAGL_WL_XFCLXX t
WHERE t.DELETE_FLAG = 'N' AND  t.uuid = #{value}

mapper.insert:
INSERT INTO YAGL_WL_XFCLXX(uuid,clbm,clmc,ssdz,ssdzmc,xzqy,cllx,cphm,clzt,sccj,jglgd,sbll,zsl,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.clbm,jdbcType=VARCHAR} clbm,#{item.clmc,jdbcType=VARCHAR} clmc,#{item.ssdz,jdbcType=VARCHAR} ssdz,#{item.ssdzmc,jdbcType=VARCHAR} ssdzmc,#{item.xzqy,jdbcType=VARCHAR} xzqy,#{item.cllx,jdbcType=DOUBLE} cllx,#{item.cphm,jdbcType=VARCHAR} cphm,#{item.clzt,jdbcType=VARCHAR} clzt,#{item.sccj,jdbcType=VARCHAR} sccj,#{item.jglgd,jdbcType=DOUBLE} jglgd,#{item.sbll,jdbcType=DOUBLE} sbll,#{item.zsl,jdbcType=DOUBLE} zsl,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_WL_XFCLXX
uuid=#{uuid}, clbm=#{clbm}, clmc=#{clmc}, ssdz=#{ssdz}, ssdzmc=#{ssdzmc}, xzqy=#{xzqy}, cllx=#{cllx}, cphm=#{cphm}, clzt=#{clzt}, sccj=#{sccj}, jglgd=#{jglgd}, sbll=#{sbll}, zsl=#{zsl}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}
