mapper.insert:
INSERT INTO YAGL_ZDBW(zdbwid,zddwid,zdbwlx,zdbwmc,zdbwwz,wxxfx,zysx,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() zdbwid, A.* FROM (
SELECT #{item.zdbwid,jdbcType=VARCHAR} zdbwid,#{item.zddwid,jdbcType=VARCHAR} zddwid,#{item.zdbwlx,jdbcType=VARCHAR} zdbwlx,#{item.zdbwmc,jdbcType=VARCHAR} zdbwmc,#{item.zdbwwz,jdbcType=VARCHAR} zdbwwz,#{item.wxxfx,jdbcType=VARCHAR} wxxfx,#{item.zysx,jdbcType=VARCHAR} zysx,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_ZDBW
zdbwid=#{zdbwid}, zddwid=#{zddwid}, zdbwlx=#{zdbwlx}, zdbwmc=#{zdbwmc}, zdbwwz=#{zdbwwz}, wxxfx=#{wxxfx}, zysx=#{zysx}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE ZDBWID = #{zdbwid}

mapper.select:
SELECT , c2.code_name zdbwlxmc, t2.uuid,t2.zdbwid,t2.jzjg,t2.zdmj,t2.zzgd,t2.zzzc,t2.jsfzr,t2.jsfzrdh,t2.ylxx,t2.cwxx,t2.gylc,t2.jdh, c4.code_name jzjgmc FROM YAGL_ZDBW t LEFT JOIN YAGL_ZDBW_ZZL t2 ON(t.ZDBWID=t2.ZDBWID AND t2.DELETE_FLAG='N')
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'ZDBWLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.zdbwlx = c2.CODE_VALUE
Left Join SYS_CODELIST c3 on c3.CODETYPE = 'JZJG' And c3.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c4 On c4.CODEID = c3.CODEID And c4.DELETE_FLAG = 'N' and t2.jzjg = c4.CODE_VALUE WHERE t.DELETE_FLAG = 'N' AND t.ZDBWLX = '20' AND t.ZDDWID = #{value}

SELECT , c2.code_name zdbwlxmc,
FROM YAGL_ZDBW t LEFT JOIN YAGL_ZDBW_CGL t2 ON(t.ZDBWID=t2.ZDBWID AND t2.DELETE_FLAG='N')
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'ZDBWLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.zdbwlx = c2.CODE_VALUE WHERE t.DELETE_FLAG = 'N' AND t.ZDBWLX = '30' AND t.ZDDWID = #{value}
