mapper.select:

SELECT 

FROM YAGL_YA_YDTS t 
WHERE t.DELETE_FLAG = 'N' AND  t.UUID = #{value}

mapper.insert:
INSERT INTO YAGL_YA_YDTS(uuid,zqid,zsyd,tbjs,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.zqid,jdbcType=VARCHAR} zqid,#{item.zsyd,jdbcType=VARCHAR} zsyd,#{item.tbjs,jdbcType=VARCHAR} tbjs,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_YA_YDTS
uuid=#{uuid}, zqid=#{zqid}, zsyd=#{zsyd}, tbjs=#{tbjs}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}

mapper.select:
SELECT

FROM YAGL_YA_YDTS t
WHERE t.DELETE_FLAG = 'N' AND  t.ZQID = #{value}
