) A

mapper.update:
UPDATE SY_SYJBXX
id=#{id}, sybh=#{sybh}, symc=#{symc}, sydz=#{sydz}, sylx=#{sylx}, sysxxxid=#{sysxxxid}, jgid=#{jgid}, qsxs=#{qsxs}, kyzt=#{kyzt}, syzt=#{syzt}, xz=#{xz}, czyid=#{czyid}, shzt=#{shzt}, zpwjurl=#{zpwjurl}, gis_x=#{gisX}, gis_y=#{gisY}, gis_h=#{gisH}, glid=#{glid}, ssld=#{ssld}, jzsj=TO_DATE(#{jzsj},'yyyy-MM-dd HH24:mi:ss'), qysj=TO_DATE(#{qysj},'yyyy-MM-dd HH24:mi:ss'), fwtd=#{fwtd}, fwtn=#{fwtn}, fwtx=#{fwtx}, fwtb=#{fwtb}, blgxd=#{blgxd}, blgxn=#{blgxn}, blgxx=#{blgxx}, blgxb=#{blgxb}, jlzt=#{jlzt}, cszt=#{cszt}, sjbb=#{sjbb}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), ywxtbsid=#{ywxtbsid}, bz=#{bz}, dwlxdm=#{dwlxdm}, dwid=#{dwid}, vercol=TO_DATE(#{vercol},'yyyy-MM-dd HH24:mi:ss'), pkid=#{pkid}, tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}

