mapper.select:

SELECT 

FROM SY_TRSYXX t 
WHERE t.JLZT = 0 AND  t.PKID = #{value}

mapper.insert:
INSERT INTO SY_TRSYXX(pkid,id,trsymc,trsywz,trsylx,sz,ywksq,sjbh,trsyrl,trsymj,jgid,czyid,jlzt,cszt,sjc,bz,cjsj,sjbb,ywxtbsid,vercol,tbjdh,tbsjc,reserve1,reserve2,reserve3) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.id,jdbcType=VARCHAR} id,#{item.trsymc,jdbcType=VARCHAR} trsymc,#{item.trsywz,jdbcType=VARCHAR} trsywz,#{item.trsylx,jdbcType=VARCHAR} trsylx,#{item.sz,jdbcType=VARCHAR} sz,#{item.ywksq,jdbcType=VARCHAR} ywksq,#{item.sjbh,jdbcType=VARCHAR} sjbh,#{item.trsyrl,jdbcType=DOUBLE} trsyrl,#{item.trsymj,jdbcType=DOUBLE} trsymj,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.czyid,jdbcType=VARCHAR} czyid,#{item.jlzt,jdbcType=DOUBLE} jlzt,#{item.cszt,jdbcType=DOUBLE} cszt,#{item.sjc,jdbcType=DATE} sjc,#{item.bz,jdbcType=VARCHAR} bz,#{item.cjsj,jdbcType=DATE} cjsj,#{item.sjbb,jdbcType=DOUBLE} sjbb,#{item.ywxtbsid,jdbcType=VARCHAR} ywxtbsid,#{item.vercol,jdbcType=DATE} vercol,#{item.tbjdh,jdbcType=VARCHAR} tbjdh,#{item.tbsjc,jdbcType=DATE} tbsjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3 FROM DUAL
) A

mapper.update:
UPDATE SY_TRSYXX
pkid=#{pkid}, id=#{id}, trsymc=#{trsymc}, trsywz=#{trsywz}, trsylx=#{trsylx}, sz=#{sz}, ywksq=#{ywksq}, sjbh=#{sjbh}, trsyrl=#{trsyrl}, trsymj=#{trsymj}, jgid=#{jgid}, czyid=#{czyid}, jlzt=#{jlzt}, cszt=#{cszt}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), sjbb=#{sjbb}, ywxtbsid=#{ywxtbsid}, vercol=TO_DATE(#{vercol},'yyyy-MM-dd HH24:mi:ss'), tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
