mapper.insert:
INSERT INTO YAGL_XFDZ(dzid,sjdzid,dzlx,dzbm,dzmc,dzjc,dzdz,lon,lat,xzqh,lxr,lxdh,czhm,zqcls,zbqcs,mhjzl,gxsys,gxzddws,xqmj,xqfw,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4,gis_x,gis_y) SELECT SYS_GUID() dzid, A.* FROM (
SELECT #{item.dzid,jdbcType=VARCHAR} dzid,#{item.sjdzid,jdbcType=VARCHAR} sjdzid,#{item.dzlx,jdbcType=VARCHAR} dzlx,#{item.dzbm,jdbcType=VARCHAR} dzbm,#{item.dzmc,jdbcType=VARCHAR} dzmc,#{item.dzjc,jdbcType=VARCHAR} dzjc,#{item.dzdz,jdbcType=VARCHAR} dzdz,#{item.lon,jdbcType=DOUBLE} lon,#{item.lat,jdbcType=DOUBLE} lat,#{item.xzqh,jdbcType=VARCHAR} xzqh,#{item.lxr,jdbcType=VARCHAR} lxr,#{item.lxdh,jdbcType=VARCHAR} lxdh,#{item.czhm,jdbcType=VARCHAR} czhm,#{item.zqcls,jdbcType=DOUBLE} zqcls,#{item.zbqcs,jdbcType=DOUBLE} zbqcs,#{item.mhjzl,jdbcType=DOUBLE} mhjzl,#{item.gxsys,jdbcType=DOUBLE} gxsys,#{item.gxzddws,jdbcType=DOUBLE} gxzddws,#{item.xqmj,jdbcType=DOUBLE} xqmj,#{item.xqfw,jdbcType=VARCHAR} xqfw,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4,#{item.gisX,jdbcType=DOUBLE} gis_x,#{item.gisY,jdbcType=DOUBLE} gis_y FROM DUAL
) A

mapper.update:
UPDATE YAGL_XFDZ
sjdzid=#{sjdzid}, dzlx=#{dzlx}, dzbm=#{dzbm}, dzmc=#{dzmc}, dzjc=#{dzjc}, dzdz=#{dzdz}, lon=#{lon}, lat=#{lat}, xzqh=#{xzqh}, lxr=#{lxr}, lxdh=#{lxdh}, czhm=#{czhm}, zqcls=#{zqcls}, zbqcs=#{zbqcs}, mhjzl=#{mhjzl}, gxsys=#{gxsys}, gxzddws=#{gxzddws}, xqmj=#{xqmj}, xqfw=#{xqfw}, delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss'), gis_x=#{gisX}, gis_y=#{gisY}
WHERE dzid = #{dzid}
