mapper.select:

SELECT 

FROM SY_XFSCSXXX t 
WHERE t.JLZT = 0 AND  t.PKID = #{value}

mapper.insert:
INSERT INTO SY_XFSCSXXX(pkid,id,sybgc,rl,jsll,gwxs,tcwz,tcsl,jgid,jlzt,cszt,sjbb,cjsj,ywxtbsid,sjc,bz,vercol,tbjdh,tbsjc,reserve1,reserve2,reserve3) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.id,jdbcType=VARCHAR} id,#{item.sybgc,jdbcType=DOUBLE} sybgc,#{item.rl,jdbcType=DOUBLE} rl,#{item.jsll,jdbcType=DOUBLE} jsll,#{item.gwxs,jdbcType=VARCHAR} gwxs,#{item.tcwz,jdbcType=VARCHAR} tcwz,#{item.tcsl,jdbcType=DOUBLE} tcsl,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.jlzt,jdbcType=DOUBLE} jlzt,#{item.cszt,jdbcType=DOUBLE} cszt,#{item.sjbb,jdbcType=DOUBLE} sjbb,#{item.cjsj,jdbcType=DATE} cjsj,#{item.ywxtbsid,jdbcType=VARCHAR} ywxtbsid,#{item.sjc,jdbcType=DATE} sjc,#{item.bz,jdbcType=VARCHAR} bz,#{item.vercol,jdbcType=DATE} vercol,#{item.tbjdh,jdbcType=VARCHAR} tbjdh,#{item.tbsjc,jdbcType=DATE} tbsjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3 FROM DUAL
) A

mapper.update:
UPDATE SY_XFSCSXXX
pkid=#{pkid}, id=#{id}, sybgc=#{sybgc}, rl=#{rl}, jsll=#{jsll}, gwxs=#{gwxs}, tcwz=#{tcwz}, tcsl=#{tcsl}, jgid=#{jgid}, jlzt=#{jlzt}, cszt=#{cszt}, sjbb=#{sjbb}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), ywxtbsid=#{ywxtbsid}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, vercol=TO_DATE(#{vercol},'yyyy-MM-dd HH24:mi:ss'), tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
