mapper.select:

SELECT 

FROM SY_XFSHSXXX t 
WHERE t.jlzt = 1 AND  t.ID = #{value}

mapper.insert:
INSERT INTO SY_XFSHSXXX(id,gwid,gdzj,gwyl,shgd,ll,jsgzj,csgzj,jscd,jgid,jlzt,cszt,sjbb,cjsj,ywxtbsid,sjc,bz,tbjdh,tbsjc,reserve1,reserve2,reserve3,pkid,vercol) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.id,jdbcType=VARCHAR} id,#{item.gwid,jdbcType=VARCHAR} gwid,#{item.gdzj,jdbcType=DOUBLE} gdzj,#{item.gwyl,jdbcType=DOUBLE} gwyl,#{item.shgd,jdbcType=DOUBLE} shgd,#{item.ll,jdbcType=DOUBLE} ll,#{item.jsgzj,jdbcType=DOUBLE} jsgzj,#{item.csgzj,jdbcType=DOUBLE} csgzj,#{item.jscd,jdbcType=VARCHAR} jscd,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.jlzt,jdbcType=DOUBLE} jlzt,#{item.cszt,jdbcType=DOUBLE} cszt,#{item.sjbb,jdbcType=DOUBLE} sjbb,#{item.cjsj,jdbcType=DATE} cjsj,#{item.ywxtbsid,jdbcType=VARCHAR} ywxtbsid,#{item.sjc,jdbcType=DATE} sjc,#{item.bz,jdbcType=VARCHAR} bz,#{item.tbjdh,jdbcType=VARCHAR} tbjdh,#{item.tbsjc,jdbcType=DATE} tbsjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,#{item.vercol,jdbcType=DATE} vercol FROM DUAL
) A

mapper.update:
UPDATE SY_XFSHSXXX
id=#{id}, gwid=#{gwid}, gdzj=#{gdzj}, gwyl=#{gwyl}, shgd=#{shgd}, ll=#{ll}, jsgzj=#{jsgzj}, csgzj=#{csgzj}, jscd=#{jscd}, jgid=#{jgid}, jlzt=#{jlzt}, cszt=#{cszt}, sjbb=#{sjbb}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), ywxtbsid=#{ywxtbsid}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss'), pkid=#{pkid}, vercol=TO_DATE(#{vercol},'yyyy-MM-dd HH24:mi:ss')
WHERE ID = #{id}
