mapper.select:

SELECT 

FROM SY_XFSHSXXX t 
WHERE t.JLZT = 0 AND  t.ID = #{value}

mapper.insert:
INSERT INTO SY_XFSHSXXX(pkid,id,所属管网,管网直径,管网压力,水鹤高度,流量,进水管直径,出水管直径,加水车道,机构id,记录状态,传输状态,数据版本,创建时间,业务系统部署id,时间戳,备注,版本戳,tbjdh,tbsjc,reserve1,reserve2,reserve3) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.id,jdbcType=VARCHAR} id,#{item.所属管网,jdbcType=VARCHAR} 所属管网,#{item.管网直径,jdbcType=DOUBLE} 管网直径,#{item.管网压力,jdbcType=DOUBLE} 管网压力,#{item.水鹤高度,jdbcType=DOUBLE} 水鹤高度,#{item.流量,jdbcType=DOUBLE} 流量,#{item.进水管直径,jdbcType=DOUBLE} 进水管直径,#{item.出水管直径,jdbcType=DOUBLE} 出水管直径,#{item.加水车道,jdbcType=VARCHAR} 加水车道,#{item.机构id,jdbcType=VARCHAR} 机构id,#{item.记录状态,jdbcType=DOUBLE} 记录状态,#{item.传输状态,jdbcType=DOUBLE} 传输状态,#{item.数据版本,jdbcType=DOUBLE} 数据版本,#{item.创建时间,jdbcType=DATE} 创建时间,#{item.业务系统部署id,jdbcType=VARCHAR} 业务系统部署id,#{item.时间戳,jdbcType=DATE} 时间戳,#{item.备注,jdbcType=VARCHAR} 备注,#{item.版本戳,jdbcType=DATE} 版本戳,#{item.tbjdh,jdbcType=VARCHAR} tbjdh,#{item.tbsjc,jdbcType=DATE} tbsjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3 FROM DUAL
) A

mapper.update:
UPDATE SY_XFSHSXXX
pkid=#{pkid}, id=#{id}, 所属管网=#{所属管网}, 管网直径=#{管网直径}, 管网压力=#{管网压力}, 水鹤高度=#{水鹤高度}, 流量=#{流量}, 进水管直径=#{进水管直径}, 出水管直径=#{出水管直径}, 加水车道=#{加水车道}, 机构id=#{机构id}, 记录状态=#{记录状态}, 传输状态=#{传输状态}, 数据版本=#{数据版本}, 创建时间=TO_DATE(#{创建时间},'yyyy-MM-dd HH24:mi:ss'), 业务系统部署id=#{业务系统部署id}, 时间戳=TO_DATE(#{时间戳},'yyyy-MM-dd HH24:mi:ss'), 备注=#{备注}, 版本戳=TO_DATE(#{版本戳},'yyyy-MM-dd HH24:mi:ss'), tbjdh=#{tbjdh}, tbsjc=TO_DATE(#{tbsjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
