mapper.select:

SELECT 

FROM YAGL_XFSY t 
WHERE t.DELETE_FLAG = 'N' AND  t.UUID = #{value}

mapper.insert:
INSERT INTO YAGL_XFSY(uuid,sybh,symc,sydz,sylx,sysxxxid,gxdz,gxdzmc,xzqy,qsxs,kyzt,xz,gis_x,gis_y,gis_h,glid,bd_lon,bd_lat,ssld,jzsj,qysj,blgxd,blgxn,blgxx,blgxb,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,delete_flag,datasource,bz,jdh,sjc,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.sybh,jdbcType=VARCHAR} sybh,#{item.symc,jdbcType=VARCHAR} symc,#{item.sydz,jdbcType=VARCHAR} sydz,#{item.sylx,jdbcType=VARCHAR} sylx,#{item.sysxxxid,jdbcType=VARCHAR} sysxxxid,#{item.gxdz,jdbcType=VARCHAR} gxdz,#{item.gxdzmc,jdbcType=VARCHAR} gxdzmc,#{item.xzqy,jdbcType=VARCHAR} xzqy,#{item.qsxs,jdbcType=VARCHAR} qsxs,#{item.kyzt,jdbcType=VARCHAR} kyzt,#{item.xz,jdbcType=VARCHAR} xz,#{item.gisX,jdbcType=DOUBLE} gis_x,#{item.gisY,jdbcType=DOUBLE} gis_y,#{item.gisH,jdbcType=DOUBLE} gis_h,#{item.glid,jdbcType=VARCHAR} glid,#{item.bdLon,jdbcType=DOUBLE} bd_lon,#{item.bdLat,jdbcType=DOUBLE} bd_lat,#{item.ssld,jdbcType=VARCHAR} ssld,#{item.jzsj,jdbcType=DATE} jzsj,#{item.qysj,jdbcType=DATE} qysj,#{item.blgxd,jdbcType=VARCHAR} blgxd,#{item.blgxn,jdbcType=VARCHAR} blgxn,#{item.blgxx,jdbcType=VARCHAR} blgxx,#{item.blgxb,jdbcType=VARCHAR} blgxb,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,'N' delete_flag,#{item.datasource,jdbcType=VARCHAR} datasource,#{item.bz,jdbcType=VARCHAR} bz,#{item.jdh,jdbcType=VARCHAR} jdh,#{item.sjc,jdbcType=DATE} sjc,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE YAGL_XFSY
uuid=#{uuid}, sybh=#{sybh}, symc=#{symc}, sydz=#{sydz}, sylx=#{sylx}, sysxxxid=#{sysxxxid}, gxdz=#{gxdz}, gxdzmc=#{gxdzmc}, xzqy=#{xzqy}, qsxs=#{qsxs}, kyzt=#{kyzt}, xz=#{xz}, gis_x=#{gisX}, gis_y=#{gisY}, gis_h=#{gisH}, glid=#{glid}, bd_lon=#{bdLon}, bd_lat=#{bdLat}, ssld=#{ssld}, jzsj=TO_DATE(#{jzsj},'yyyy-MM-dd HH24:mi:ss'), qysj=TO_DATE(#{qysj},'yyyy-MM-dd HH24:mi:ss'), blgxd=#{blgxd}, blgxn=#{blgxn}, blgxx=#{blgxx}, blgxb=#{blgxb}, cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', datasource=#{datasource}, bz=#{bz}, jdh=#{jdh}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
