mapper.select:

SELECT 

FROM SY_XHSSXXX t 
WHERE t.JLZT = 1 AND  t.ID = #{value}

mapper.insert:
INSERT INTO SY_XHSSXXX(id,gwid,szxs,gsdw,lxfs,gwxs,gwzj,gwyl,jkxs,ll,jgid,jlzt,cszt,sjbb,cjsj,ywxtbsid,sjc,bz,vercol) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.id,jdbcType=VARCHAR} id,#{item.gwid,jdbcType=VARCHAR} gwid,#{item.szxs,jdbcType=VARCHAR} szxs,#{item.gsdw,jdbcType=VARCHAR} gsdw,#{item.lxfs,jdbcType=VARCHAR} lxfs,#{item.gwxs,jdbcType=VARCHAR} gwxs,#{item.gwzj,jdbcType=DOUBLE} gwzj,#{item.gwyl,jdbcType=DOUBLE} gwyl,#{item.jkxs,jdbcType=VARCHAR} jkxs,#{item.ll,jdbcType=DOUBLE} ll,#{item.jgid,jdbcType=VARCHAR} jgid,#{item.jlzt,jdbcType=DOUBLE} jlzt,#{item.cszt,jdbcType=DOUBLE} cszt,#{item.sjbb,jdbcType=DOUBLE} sjbb,#{item.cjsj,jdbcType=DATE} cjsj,#{item.ywxtbsid,jdbcType=VARCHAR} ywxtbsid,#{item.sjc,jdbcType=DATE} sjc,#{item.bz,jdbcType=VARCHAR} bz,#{item.vercol,jdbcType=VARCHAR} vercol FROM DUAL
) A

mapper.update:
UPDATE SY_XHSSXXX
id=#{id}, gwid=#{gwid}, szxs=#{szxs}, gsdw=#{gsdw}, lxfs=#{lxfs}, gwxs=#{gwxs}, gwzj=#{gwzj}, gwyl=#{gwyl}, jkxs=#{jkxs}, ll=#{ll}, jgid=#{jgid}, jlzt=#{jlzt}, cszt=#{cszt}, sjbb=#{sjbb}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), ywxtbsid=#{ywxtbsid}, sjc=TO_DATE(#{sjc},'yyyy-MM-dd HH24:mi:ss'), bz=#{bz}, vercol=#{vercol}
WHERE PKID = #{pkid}
