mapper.select:
SELECT , c2.code_name sjztmc, c4.code_name shztmc FROM CFPA_QY_JBXX t
Left Join SYS_CODELIST c1 on c1.CODETYPE = 'SJZT' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and t.sjzt = c2.CODE_VALUE
Left Join SYS_CODELIST c3 on c3.CODETYPE = 'SHZT' And c3.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c4 On c4.CODEID = c3.CODEID And c4.DELETE_FLAG = 'N' and t.shzt = c4.CODE_VALUE WHERE t.DELETE_FLAG = 'N' AND t.QYID = #{value}

mapper.insert:
INSERT INTO
CFPA_QY_JBXX(qyid,userid,zwgsmc,ywgsmc,frdb,frdbdh,yjdzsheng,yjdzshi,yjdzxx,bgdh,cz,lxr,lxrsj,wz,dzyx,yyzz,sjzt,shzt,shrid,shrmc,shsj,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() qyid, A.*
FROM (

    SELECT
    #{item.qyid,jdbcType=VARCHAR} qyid,#{item.userid,jdbcType=VARCHAR} userid,#{item.zwgsmc,jdbcType=VARCHAR}
    zwgsmc,#{item.ywgsmc,jdbcType=VARCHAR} ywgsmc,#{item.frdb,jdbcType=VARCHAR}
    frdb,#{item.frdbdh,jdbcType=VARCHAR} frdbdh,#{item.yjdzsheng,jdbcType=VARCHAR}
    yjdzsheng,#{item.yjdzshi,jdbcType=VARCHAR} yjdzshi,#{item.yjdzxx,jdbcType=VARCHAR}
    yjdzxx,#{item.bgdh,jdbcType=VARCHAR} bgdh,#{item.cz,jdbcType=VARCHAR} cz,#{item.lxr,jdbcType=VARCHAR}
    lxr,#{item.lxrsj,jdbcType=VARCHAR} lxrsj,#{item.wz,jdbcType=VARCHAR} wz,#{item.dzyx,jdbcType=VARCHAR}
    dzyx,#{item.yyzz,jdbcType=VARCHAR} yyzz,#{item.sjzt,jdbcType=VARCHAR} sjzt,#{item.shzt,jdbcType=VARCHAR}
    shzt,#{item.shrid,jdbcType=VARCHAR} shrid,#{item.shrmc,jdbcType=VARCHAR} shrmc,#{item.shsj,jdbcType=DATE}
    shsj,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR}
    cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR}
    xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE CFPA_QY_JBXX
userid=#{userid}, zwgsmc=#{zwgsmc}, ywgsmc=#{ywgsmc}, frdb=#{frdb}, frdbdh=#{frdbdh}, yjdzsheng=#{yjdzsheng}, yjdzshi=#{yjdzshi}, yjdzxx=#{yjdzxx}, bgdh=#{bgdh}, cz=#{cz}, lxr=#{lxr}, lxrsj=#{lxrsj}, wz=#{wz}, dzyx=#{dzyx}, yyzz=#{yyzz}, sjzt=#{sjzt}, shzt=#{shzt}, shrid=#{shrid}, shrmc=#{shrmc}, shsj=TO_DATE(#{shsj},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE QYID = #{qyid}
