mapper.select:
SELECT

FROM CFPA_QY_ZWYX t
WHERE t.DELETE_FLAG = 'N' AND t.UUID = #{value}

mapper.insert:
INSERT INTO
CFPA_QY_ZWYX(uuid,qyid,bzzwgs,sngdzw,swgdzw,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4)
SELECT SYS_GUID() uuid, A.*
FROM (

    SELECT
    #{item.uuid,jdbcType=VARCHAR} uuid,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.bzzwgs,jdbcType=DOUBLE}
    bzzwgs,#{item.sngdzw,jdbcType=DOUBLE} sngdzw,#{item.swgdzw,jdbcType=DOUBLE} swgdzw,'N'
    delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR}
    cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR}
    xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR}
    reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR}
    reserve3,#{item.reserve4,jdbcType=DATE} reserve4
    FROM DUAL

) A

mapper.update:
UPDATE CFPA_QY_ZWYX
uuid=#{uuid}, qyid=#{qyid}, bzzwgs=#{bzzwgs}, sngdzw=#{sngdzw}, swgdzw=#{swgdzw}, delete_flag='N', cjrid=#{cjrid}, cjrmc=#{cjrmc}, cjsj=TO_DATE(#{cjsj},'yyyy-MM-dd HH24:mi:ss'), xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss')
WHERE UUID = #{uuid}

mapper.select:
SELECT gg.code_name cplxmc, COUNT(1) AS czqysl, SUM(sngdzw) + SUM(swgdzw) AS gdzwmj, SUM(bzzwgs) AS bwzwgssl from ( SELECT t.qyid, sngdzw, swgdzw, c2.code_name,t.bzzwgs FROM CFPA_QY_ZWYX t Left Join CFPA_QY_CPJS k on k.qyid = t.qyid Left Join SYS_CODELIST c1 on c1.CODETYPE = 'CPLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and CONCAT(substr(k.cplx,0,1),'000')= c2.CODE_VALUE
WHERE t.delete_flag = 'N' AND t.qyid = k.qyid And k.DELETE_FLAG = 'N'
) gg group by gg.code_name

SELECT gg.code_name cplxmc, COUNT(1) AS czqysl, SUM(sngdzw) + SUM(swgdzw) AS gdzwmj, SUM(bzzwgs) AS bwzwgssl from ( SELECT t.qyid, sngdzw, swgdzw, c2.code_name,t.bzzwgs FROM CFPA_QY_ZWYX t Left Join CFPA_QY_CPJS k on k.qyid = t.qyid Left Join SYS_CODELIST c1 on c1.CODETYPE = 'CPLX' And c1.DELETE_FLAG = 'N' Left Join SYS_CODELIST_DETAIL c2 On c1.CODEID = c2.CODEID And c2.DELETE_FLAG = 'N' and CONCAT(substr(k.cplx,0,1),'000')= c2.CODE_VALUE
WHERE t.delete_flag = 'N' AND t.qyid = k.qyid And k.DELETE_FLAG = 'N'
) gg group by gg.code_name
