mapper.select:

SELECT 

FROM SYS_MENU t 
WHERE t.DELETE_FLAG = 'N' AND  t.menuid = #{value}

mapper.insert:
INSERT INTO SYS_MENU(menuid,menuname,menuurl,menuinfo,parentid,isleaf,create_id,create_name,create_time,alter_id,alter_name,alter_time,reserve1,reserve2,reserve3,delete_flag,seqno) SELECT SYS_GUID() menuid, A.* FROM (
SELECT #{item.menuname,jdbcType=VARCHAR} menuname,#{item.menuurl,jdbcType=VARCHAR} menuurl,#{item.menuinfo,jdbcType=VARCHAR} menuinfo,#{item.parentid,jdbcType=VARCHAR} parentid,#{item.isleaf,jdbcType=VARCHAR} isleaf,#{item.createId,jdbcType=VARCHAR} create_id,#{item.createName,jdbcType=VARCHAR} create_name,SYSDATE create_time,#{item.alterId,jdbcType=VARCHAR} alter_id,#{item.alterName,jdbcType=VARCHAR} alter_name,SYSDATE alter_time,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=DATE} reserve3,'N' delete_flag,#{item.seqno,jdbcType=Integer} seqno FROM DUAL
) A

mapper.update:
UPDATE SYS_MENU
menuid=#{menuid}, menuname=#{menuname}, menuurl=#{menuurl}, menuinfo=#{menuinfo}, parentid=#{parentid}, isleaf=#{isleaf}, create_id=#{createId}, create_name=#{createName}, create_time=TO_DATE(#{createTime},'yyyy-MM-dd HH24:mi:ss'), alter_id=#{alterId}, alter_name=#{alterName}, alter_time=TO_DATE(TO_CHAR(SYSDATE,'yyyy-MM-dd HH24:mi:ss'),'yyyy-MM-dd HH24:mi:ss'), reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=TO_DATE(#{reserve3},'yyyy-MM-dd HH24:mi:ss'), delete_flag='N' seqno=#{seqno}
WHERE menuid = #{menuid}
