mapper.select:

SELECT 

FROM CFPA_ZW_FLXX t 
WHERE t.DELETE_FLAG = 'N' AND  t.UUID = #{value}

mapper.insert:
INSERT INTO CFPA_ZW_FLXX(uuid,zwfl,kkfl,flmj,fljg,delete_flag,cjrid,cjrmc,cjsj,xgrid,xgrmc,xgsj,reserve1,reserve2,reserve3,reserve4) SELECT SYS_GUID() uuid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.zwfl,jdbcType=VARCHAR} zwfl,#{item.kkfl,jdbcType=VARCHAR} kkfl,#{item.flmj,jdbcType=DOUBLE} flmj,#{item.fljg,jdbcType=DOUBLE} fljg,'N' delete_flag,#{item.cjrid,jdbcType=VARCHAR} cjrid,#{item.cjrmc,jdbcType=VARCHAR} cjrmc,#{item.cjsj,jdbcType=DATE} cjsj,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4 FROM DUAL
) A

mapper.update:
UPDATE CFPA_ZW_FLXX
zwfl=#{zwfl}, kkfl=#{kkfl}, flmj=#{flmj}, fljg=#{fljg}, delete_flag=#{deleteFlag},
xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=sysdate, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss'), fljg_eng=#{fljgEng}
WHERE UUID = #{uuid}
