mapper.select:

SELECT 

FROM CFPA_ZW_LOG t 
WHERE t.DELETE_FLAG = 'N' AND  t.PKID = #{value}

mapper.insert:
INSERT INTO CFPA_ZW_LOG(uuid,reserve1,reserve2,reserve3,reserve4,yqyid,zwuuid,zwh,qyid,czlx,czrid,czrmc,czsj,rzsj,ffmc,delete_flag,xgrid,xgrmc,xgsj) SELECT SYS_GUID() pkid, A.* FROM (
SELECT #{item.uuid,jdbcType=VARCHAR} uuid,#{item.reserve1,jdbcType=VARCHAR} reserve1,#{item.reserve2,jdbcType=VARCHAR} reserve2,#{item.reserve3,jdbcType=VARCHAR} reserve3,#{item.reserve4,jdbcType=DATE} reserve4,#{item.yqyid,jdbcType=VARCHAR} yqyid,#{item.zwuuid,jdbcType=VARCHAR} zwuuid,#{item.zwh,jdbcType=VARCHAR} zwh,#{item.qyid,jdbcType=VARCHAR} qyid,#{item.czlx,jdbcType=VARCHAR} czlx,#{item.czrid,jdbcType=VARCHAR} czrid,#{item.czrmc,jdbcType=VARCHAR} czrmc,#{item.czsj,jdbcType=DATE} czsj,#{item.rzsj,jdbcType=DATE} rzsj,#{item.ffmc,jdbcType=VARCHAR} ffmc,'N' delete_flag,#{item.xgrid,jdbcType=VARCHAR} xgrid,#{item.xgrmc,jdbcType=VARCHAR} xgrmc,#{item.xgsj,jdbcType=DATE} xgsj FROM DUAL
) A

mapper.update:
UPDATE CFPA_ZW_LOG
uuid=#{uuid}, reserve1=#{reserve1}, reserve2=#{reserve2}, reserve3=#{reserve3}, reserve4=TO_DATE(#{reserve4},'yyyy-MM-dd HH24:mi:ss'), yqyid=#{yqyid}, zwuuid=#{zwuuid}, zwh=#{zwh}, qyid=#{qyid}, czlx=#{czlx}, czrid=#{czrid}, czrmc=#{czrmc}, czsj=TO_DATE(#{czsj},'yyyy-MM-dd HH24:mi:ss'), rzsj=TO_DATE(#{rzsj},'yyyy-MM-dd HH24:mi:ss'), ffmc=#{ffmc}, delete_flag='N', xgrid=#{xgrid}, xgrmc=#{xgrmc}, xgsj=TO_DATE(#{xgsj},'yyyy-MM-dd HH24:mi:ss')
WHERE PKID = #{pkid}
